import React, { useState } from 'react';

// Sample data for gallery items
const galleryItems = [
    { id: 1, type: 'event', date: '2024-01-15', src: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSCQEZwWCbjMFzpoNOAwVu0RDkLLAb_DScJdQ&s', alt: 'Event 1' },
    { id: 2, type: 'activity', date: '2024-02-20', src: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQEsDlatj_e-Pb8U30fUlf6vdrYLrrLM27evg&s', alt: "activity 1" },
    { id: 3, type: 'infrastructure', date: '2024-03-10', src: 'data:image/jpeg;base64,', alt: 'Infrastructure 1' },
    { id: 4, type: 'event', date: '2024-01-22', src: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSZ-2n04MYItc0isXpdKgYr6zrUXKln1TKCmg&s', alt: 'Event 2' },
    { id: 5, type: 'activity', date: '2024-02-25', src: 'data:image/jpeg;base64,', alt: 'Activity 2' },
    { id: 6, type: 'infrastructure', date: '2024-03-15', src: 'data:image/jpeg;base64,', alt: 'Infrastructure 2' },
    { id: 7, type: 'video', date: '2024-03-15', src: 'https://www.youtube.com/embed/oLSsKCwNPrg', alt: 'video' },
    { id: 8, type: 'video', date: '2024-03-15', src: 'https://www.youtube.com/embed/FgbGqWg2oj4', alt: 'video' }
    // Add more items as needed
];

export default function Gallery() {
    const [filter, setFilter] = useState('all');

    const filteredItems = filter === 'all'
        ? galleryItems
        : galleryItems.filter(item => item.type === filter);

    return (
        <div style={{ padding: '20px', backgroundColor: '#f4f4f4' }}>
            <h1 style={{ textAlign: 'center', marginBottom: '20px' }}>Gallery</h1>

            {/* Filtering Controls */}
            <div style={{ textAlign: 'center', marginBottom: '20px' }}>
                <button className='btn btn-secondary' onClick={() => setFilter('all')} style={{ margin: '5px' }}>All</button>
                <button className='btn btn-secondary' onClick={() => setFilter('event')} style={{ margin: '5px' }}>Events</button>
                <button className='btn btn-secondary' onClick={() => setFilter('activity')} style={{ margin: '5px' }}>Activities</button>
                <button className='btn btn-secondary' onClick={() => setFilter('infrastructure')} style={{ margin: '5px' }}>Infrastructure</button>
                <button className='btn btn-secondary' onClick={() => setFilter('video')} style={{ margin: '5px' }}>Video</button>
            </div>

            {/* Gallery Grid */}
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: '20px' }}>
                {filteredItems.map(item => (
                    <div key={item.id} style={{ position: 'relative', overflow: 'hidden', borderRadius: '8px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' }}>
                        {item.type === 'video' ? (
                            <iframe
                                src={item.src}
                                title={item.alt}
                                style={{ width: '100%', height: '100%', border: 'none' }}

                            ></iframe>
                        ) : (
                            <img src={item.src} alt={item.alt} style={{ width: '100%', height: 'auto' }} />
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
}
